import { useState, useEffect } from 'react'
import { render } from 'react-dom';
import { useParams } from 'react-router-dom'

function App() {
    //const { id } = useParams()
    const id = 1;
    const [ allCommentData, setAllCommentData ] = useState([])

    useEffect(() => {
        //const API_URL = `http://localhost:5000/comments/id/${id}`
        const API_URL = `http://localhost:5000/comments`
        const fetchData = async () => {
            const response = await fetch(API_URL)
            const resData = await response.json()
            console.log(resData)
            setAllCommentData(resData)
        }
        fetchData()
    }, [id])

    const renderComments = allCommentData.map((comment,i)=>{
      return (
        <div key={i}>
          <p>{comment.song_name}</p>
          <p>{comment.rating}</p>
        </div>
      )
    })

    return(
      <div>
        <p></p>
        {renderComments}
      </div>
    )

}

export default App;
