import { useState } from 'react' 

function GalleryItem(props){
    let [view, setView] = useState(false)

    const simpleStyle = {
        'width': '25vw',
        'height': '20vh',
        'text-align': 'center',
        'border': '1px solid black',
        'margin': '2px'
    }
    
    const detailStyle = {
        'width': '80vw',
        'height': '20vh',
        'text-align': 'center',
        'border': '1px solid black',
        'margin': '2px',
        'backgroundRepeat': 'no-repeat',
        'backgroundSize': 'cover',
        'color': 'blue'
    }

    function testUp(){
        console.log("test pass for Update")
    }
    function testDel(){
        console.log("test pass for Delete")
    }
    const simpleView = () => {
        return (
            <div style={simpleStyle}>
                <h2>{props.item.song_name} - {props.item.rating}</h2>
                <button onClick={testUp}>Update</button>
                <button onClick={testDel}>Delete</button>
            </div>
        )
    }

    const detailView = () => {
        return (
            <div style={detailStyle}>
                <h2>{props.item.song_name}</h2>
                <h3>{props.item.rating}</h3>
                <h4>{props.item.name}</h4>
                <h4>{props.item.comment}</h4>
                <h4>{props.item.date}</h4>
                <button onClick={testUp}>Update</button>
                <button onClick={testDel}>Delete</button>
            </div>
        )
    }

    return (
        <div onClick={() =>setView(!view)} style={{'display': 'inline-block'}}>
            {view ? detailView() : simpleView()}
        </div>
    )
}

export default GalleryItem
